static ushort getshort(uchar **pp)
{
   ushort n=0;

   uchar *p = *pp;

   if (*p < 0x80) {
      n = *p++;
      *pp = p;
      return n;
   }

   n = *p & 0x7f;
   p++;

   ushort v = (ushort)*p;
   p++;
   if (v < 0x80) {
      n |= (v << 7);
   } else {
      n |= ((v & 0x7f) << 7);
      v = (ushort)*p;
      p++;
      n |= (v << 14);
   }

   *pp = p;

   return n;
}

char *getuniname(ushort ncode)
{
   static char szbuf[2][200];
   static int  ibuf=0;

   ibuf ^= 1;
   szbuf[ibuf][0] = '\0';

   uchar *pcur=auninames;

   bool bverb=0;

   while (*pcur)
   {
      // next record
      ushort ncode2 = getshort(&pcur);
      if (ncode2 != ncode) {
         while (getshort(&pcur) != 0)
            ;
         continue;
      }

      // printf("%04x ",ncode);
      while (1)
      {
         ushort nword = getshort(&pcur);
         if (!nword) break;
         // printf("%s ",adict[nword]);
         cchar *pword = adict[nword];
         if (!strcmp(pword, "SMALL"))
            pword = "SMALL  ";
         strcat(szbuf[ibuf], pword);
         strcat(szbuf[ibuf], " ");
      }
      // printf("\n");
      break;
   }

   char *psz=szbuf[ibuf];
   int   ilen=strlen(psz);
   for (; ilen>0 && psz[ilen-1]==' '; ilen--)
      psz[ilen-1]='\0';

   return szbuf[ibuf];
}

